INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42697, 3, 3, 'Fools! Our cause is righteous!', 14, 0, 100, 0, 0, 5783, 6064, 0, 'Edwin van Cleef');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42697, 4, 4, 'The Brotherhood shall prevail!', 14, 0, 100, 0, 0, 5784, 6065, 0, 'Edwin van Cleef');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42698, 0, 0, '%s jumps out of the shadows!', 16, 0, 100, 0, 0, 5780, 1327, 0, 'Defias Blackguard');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42699, 0, 0, 'There\'s nowhere left to hide, VanCleef! The Defias are THROUGH!', 14, 0, 100, 22, 0, 0, 42593, 0, 'Warrior no hide');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42699, 1, 1, 'We\'re here to finish this! Face us, coward!', 14, 0, 100, 397, 0, 0, 42594, 0, 'Warrior Face us');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42699, 2, 2, 'Victory for the Alliance! Glory to Stormwind!', 14, 0, 100, 5, 0, 0, 42602, 0, 'Victory speech');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42699, 3, 3, 'Let us return to Sentinel Hill, allies, and inform Gryan of VanCleef\'s demise!', 12, 0, 100, 396, 0, 0, 42603, 0, 'Go home speech');
