INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42928, 0, 0, 'That is quite an accusation, Moira. It\'s hardly your throne room in the first place.', 12, 0, 100, 6, 0, 0, 42007, 0, 'Muradin Bronzebeard to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42928, 1, 0, 'I know Commander Stonebreaker and I will vouch for his loyalty and his honesty. We must take his word.', 12, 0, 100, 1, 0, 0, 42008, 0, 'Muradin Bronzebeard to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42928, 2, 0, 'What cause do the Dark Irons have for taking up arms against Ironforge? Are you unable to control your own people, Moira?', 12, 0, 100, 6, 0, 0, 42012, 0, 'Muradin Bronzebeard to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42931, 0, 0, 'Help!', 0, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (42975, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Enrage');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43173, 0, 0, 'Hail, citizen. I would recommend that you speak with Watch Captain Parker. He\'s currently at the top of this tower.', 12, 0, 100, 25, 0, 0, 43164, 0, 'Redridge Garrison Watchman');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43189, 0, 0, 'This pathetic revolution ends here!', 12, 0, 100, 0, 0, 0, 43761, 0, 'Warchief Mor\'ghor to Dragonmaw Shaman');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43189, 1, 0, 'Pathetic worms. Prepare to face the Mor\'ghor Slam!', 14, 0, 100, 25, 0, 21599, 44062, 0, 'Warchief Mor\'ghor');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43189, 2, 0, 'Zaela ... traitorous fool ... have you sold your soul to the Horde? Are you and I so different? Cast your lot with Hellscream, then. See where he takes you.', 12, 0, 100, 0, 0, 21130, 44287, 0, 'Warchief Mor\'ghor to Dragonmaw Warrior');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43190, 0, 0, 'Gregor, you coward! Orcs of pure blood are dying off our shores. If you doom them, you doom us all.', 12, 0, 100, 5, 0, 0, 43239, 0, 'Zaela to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43190, 1, 0, 'Hurry, Gorthul. Undo $ghis:her; bonds.', 12, 0, 100, 0, 0, 0, 43463, 0, 'Zaela to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43191, 0, 0, 'I ... I can\'t give you any of our supplies! Our Warchief... He would...', 12, 0, 100, 274, 0, 0, 43238, 0, 'Gregor to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43191, 1, 0, 'Very well, Zaela. I trust you to do the right thing. $C, I can help your fallen warriors...', 12, 0, 100, 2, 0, 0, 43237, 0, 'Gregor to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43222, 0, 0, 'My spouse has gone missing! Please help!', 12, 7, 100, 22, 0, 0, 43745, 0, 'Redridge Citizen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43222, 0, 1, 'Townspeople are disappearing, Solomon. What are you going to do about it?', 12, 7, 100, 22, 0, 0, 43746, 0, 'Redridge Citizen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43222, 0, 2, 'It\'s time to wake up, magistrate! These aren\'t ordinary gnoll attacks!', 12, 7, 100, 25, 0, 0, 43747, 0, 'Redridge Citizen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43222, 0, 3, 'Where is the help that we requested from Stormwind?', 12, 7, 100, 4, 0, 0, 43748, 0, 'Redridge Citizen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43222, 0, 4, 'What are we paying our taxes for if your guards can\'t even keep gnolls out?', 12, 7, 100, 53, 0, 0, 43749, 0, 'Redridge Citizen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43222, 0, 5, 'My children were kidnapped while they slept! I WANT ANSWERS, SOLOMON!', 12, 7, 100, 5, 0, 0, 43750, 0, 'Redridge Citizen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43222, 0, 6, 'You better do something quick or you\'ll have a riot on your hands, Solomon!', 12, 7, 100, 53, 0, 0, 43751, 0, 'Redridge Citizen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43222, 0, 7, 'The gnolls have never been this aggressive. What\'s going on, Solomon? Tell us!', 12, 7, 100, 25, 0, 0, 43752, 0, 'Redridge Citizen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43222, 0, 8, 'How long must we stand here before our pleas are heard?', 12, 7, 100, 53, 0, 0, 43753, 0, 'Redridge Citizen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43222, 0, 9, 'Get this damned line moving!', 12, 7, 100, 15, 0, 0, 43754, 0, 'Redridge Citizen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43222, 0, 10, 'You\'re good for nothing, Solomon!', 12, 7, 100, 14, 0, 0, 43755, 0, 'Redridge Citizen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43222, 0, 11, 'You can forget about re-election, magistrate!', 12, 7, 100, 274, 0, 0, 43756, 0, 'Redridge Citizen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43222, 0, 12, 'No justice, no peace!', 12, 7, 100, 22, 0, 0, 43757, 0, 'Redridge Citizen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43222, 0, 13, 'If you don\'t do something about these gnolls, we will!', 12, 7, 100, 25, 0, 0, 43758, 0, 'Redridge Citizen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43222, 0, 14, 'Please help me! I\'m the last member of my family left. Everyone else has been kidnapped!', 12, 7, 100, 15, 0, 0, 43759, 0, 'Redridge Citizen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43222, 1, 0, 'Forget you, Solomon. I\'ll find my family by myself.', 12, 7, 100, 14, 0, 0, 43760, 0, 'Redridge Citizen');
