INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43247, 0, 4, 'What are we paying our taxes for if your guards can\'t even keep gnolls out?', 12, 7, 100, 53, 0, 0, 43749, 0, 'Redridge Citizen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43247, 0, 5, 'My children were kidnapped while they slept! I WANT ANSWERS, SOLOMON!', 12, 7, 100, 5, 0, 0, 43750, 0, 'Redridge Citizen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43247, 0, 6, 'You better do something quick or you\'ll have a riot on your hands, Solomon!', 12, 7, 100, 53, 0, 0, 43751, 0, 'Redridge Citizen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43247, 0, 7, 'The gnolls have never been this aggressive. What\'s going on, Solomon? Tell us!', 12, 7, 100, 25, 0, 0, 43752, 0, 'Redridge Citizen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43247, 0, 8, 'How long must we stand here before our pleas are heard?', 12, 7, 100, 53, 0, 0, 43753, 0, 'Redridge Citizen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43247, 0, 9, 'Get this damned line moving!', 12, 7, 100, 15, 0, 0, 43754, 0, 'Redridge Citizen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43247, 0, 10, 'You\'re good for nothing, Solomon!', 12, 7, 100, 14, 0, 0, 43755, 0, 'Redridge Citizen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43247, 0, 11, 'You can forget about re-election, magistrate!', 12, 7, 100, 274, 0, 0, 43756, 0, 'Redridge Citizen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43247, 0, 12, 'No justice, no peace!', 12, 7, 100, 22, 0, 0, 43757, 0, 'Redridge Citizen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43247, 0, 13, 'If you don\'t do something about these gnolls, we will!', 12, 7, 100, 25, 0, 0, 43758, 0, 'Redridge Citizen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43247, 0, 14, 'Please help me! I\'m the last member of my family left. Everyone else has been kidnapped!', 12, 7, 100, 15, 0, 0, 43759, 0, 'Redridge Citizen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43247, 1, 0, 'Forget you, Solomon. I\'ll find my family by myself.', 12, 7, 100, 14, 0, 0, 43760, 0, 'Redridge Citizen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43260, 0, 0, 'Quit wasting words, worm. If you want the aid of the Dragonmaw, you should\'ve come on bent knee and with offerings of gold.', 12, 0, 100, 25, 0, 0, 43306, 0, 'Warchief Mor\'ghor to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43260, 1, 0, 'Was that a THREAT? I knew Hellscream in Outland. A whimpering whelp. He is no Warchief.', 12, 0, 100, 11, 0, 0, 43307, 0, 'Warchief Mor\'ghor to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43260, 2, 0, 'Bring him on!', 12, 0, 100, 5, 0, 0, 43308, 0, 'Warchief Mor\'ghor to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43260, 3, 0, 'This is...', 12, 0, 100, 0, 0, 0, 43309, 0, 'Warchief Mor\'ghor to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43260, 4, 0, 'DRAGONMAW!!', 14, 0, 100, 0, 0, 0, 43310, 0, 'Warchief Mor\'ghor to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43260, 5, 0, 'Keep the $c alive. $gHe:She; will be our bargaining chip.', 12, 0, 100, 1, 0, 0, 43311, 0, 'Warchief Mor\'ghor to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43260, 6, 0, 'Call for the others. We will crush Hellscream\'s Horde and hurl their bodies to the sea...', 12, 0, 100, 273, 0, 0, 43312, 0, 'Warchief Mor\'ghor to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43427, 0, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43453, 0, 0, 'Brother!', 12, 0, 100, 0, 0, 0, 43966, 0, 'Tobias Mistmantle to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43453, 1, 1, 'Tell me it\'s not true, brother. Tell me you didn\'t die a murderer!', 12, 0, 100, 25, 0, 0, 43967, 0, 'Tobias Mistmantle to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43453, 2, 2, 'But why?! How could you?', 12, 0, 100, 5, 0, 0, 43973, 0, 'Tobias Mistmantle to Player');
