INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43566, 0, 0, 'Attack!', 14, 0, 100, 0, 0, 16419, 0, 0, 'Lorna Crowley');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43566, 1, 0, 'Secure the rafters!', 14, 0, 100, 0, 0, 16419, 0, 0, 'Lorna Crowley');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43566, 2, 0, 'I want two sharpshooters to stay behind and cover the deck.  Everyone else, use the ropes to rappel down.  Let\'s give them hell!', 14, 0, 100, 0, 0, 16419, 0, 0, 'Lorna Crowley');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43566, 3, 0, 'Hands up, greenskins!  My men will give you your new bearing.  You try anything funny and we\'ll fill you with lead.', 14, 0, 100, 0, 0, 16419, 0, 0, 'Lorna Crowley');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43566, 4, 0, 'The rest of you, follow me downstairs!', 14, 0, 100, 0, 0, 16419, 0, 0, 'Lorna Crowley');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43566, 5, 0, 'We\'ve got the explosives  in the furnace room.  Just hold them off now!', 12, 0, 100, 0, 0, 16419, 0, 0, 'Lorna Crowley');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43566, 6, 0, 'That\'s... one big orc.', 12, 0, 100, 0, 0, 16419, 0, 0, 'Lorna Crowley');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43566, 7, 0, 'It\'s about to blow, jump on the Wyverns!', 14, 0, 100, 0, 0, 16419, 0, 0, 'Lorna Crowley');
