INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43749, 0, 0, 'Hold your horses, friend.  We\'ll hit them when we\'re all ready.', 12, 0, 100, 0, 0, 16419, 0, 0, 'Tobias Mistmantle');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43749, 1, 0, 'Hop on a hippogryph, now\'s our chance!', 12, 0, 100, 0, 0, 16419, 0, 0, 'Tobias Mistmantle');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43797, 3, 3, 'No... NO! STOP IT!', 14, 0, 100, 15, 0, 0, 43980, 0, 'Tobias Mistmantle to Player');
