INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (43939, 0, 0, 'Mor\'ghor! You should\'ve stayed in Outland with the rest of your half-demon scum. There\'s no place for you here.', 12, 0, 100, 5, 0, 21599, 44281, 0, 'Zaela');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (44086, 0, 0, 'The... They promised that we would have security...', 12, 0, 33, 0, 0, 0, 0, 0, 'Panicked Citizen Random Saying');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (44086, 1, 1, 'It means we go from here! The screams are stronger every night.', 12, 0, 33, 0, 0, 0, 0, 0, 'Panicked Citizen Random Saying');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (44086, 2, 2, 'Why are the doors closed data?', 12, 0, 33, 0, 0, 0, 0, 0, 'Panicked Citizen Random Saying');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (44120, 0, 0, 'Silence, idiot.', 12, 0, 100, 0, 0, 0, 44459, 0, 'Sauranok the Mystic to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (44120, 2, 0, 'The Twilight\'s Hammer has agents everywhere! Kill me and a dozen take my place!', 12, 0, 100, 0, 0, 0, 44409, 0, 'Sauranok the Mystic');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (44120, 3, 0, 'My powers of sight... I beheld the future... I have seen what is to come...', 12, 0, 100, 0, 0, 0, 44410, 0, 'Sauranok the Mystic');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (44120, 4, 0, 'This world unravels. If you could see the horrors I have seen... ', 12, 0, 100, 0, 0, 0, 44411, 0, 'Sauranok the Mystic');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (44120, 5, 0, 'To serve is madness, but defiance ... impossible!', 12, 0, 100, 0, 0, 0, 44412, 0, 'Sauranok the Mystic');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (44160, 0, 0, 'Wait, uh - you put words in my mouth! Not fair!', 12, 0, 100, 0, 0, 0, 44454, 0, '');
