INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48445, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Enrage');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48494, 19, 0, 'Well of course we are, my dear Greely. And look there. The Micro Mechachicken\'s stabilization appears to be a complete success!', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48494, 21, 0, 'I haven\'t the foggiest notion what you\'re talking about, Miss Greely.', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48494, 20, 0, 'The micro mechachicken explodes.', 16, 0, 100, 0, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48494, 18, 0, 'Are we still alive?', 12, 0, 100, 0, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48494, 17, 0, 'The micro mechachicken quickly shrinks back to normal.', 16, 0, 100, 0, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48494, 15, 0, 'Here goes nothing, I always loved you, Greely!', 14, 0, 100, 0, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48494, 16, 0, 'Flipping the override switch now!', 14, 0, 100, 0, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48494, 14, 0, 'I know... I know! Where\'s the override? The override?!', 14, 0, 100, 0, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48494, 13, 0, 'Wait... something\'s wrong!', 12, 0, 100, 0, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48494, 11, 0, 'I\'m flipping the second switch!', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48494, 12, 0, 'The micro mechachicken begins to grow and Greely starts sneaking away from the machine.', 16, 0, 100, 0, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48494, 10, 0, 'Grocery store owners everywhere will owe me a percentage of their profits!', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48494, 9, 0, 'Greely, can you feel it? My moment of success is at hand!', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48494, 8, 0, 'I am now flipping the switch!', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48494, 7, 0, 'Quite right, Greely. You passed today\'s pop quiz.', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48494, 6, 0, 'Flip the switch!', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48494, 5, 0, 'I\'ll soon be revered as the greatest goblin tinker of all times! Besides, that negligence case involving the creation of the gilgoblins will never stick.', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48494, 4, 0, 'I am going to end the horrible problem of food storage cutting into the profits of the Bilgewater Cartel!', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48494, 3, 0, 'Thank you. Now behold as I stabilize the highly unstable miniaturization process.', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48494, 2, 0, 'Excellent. Now back away from the Ultra Concave Fissionable Testing Platform. Slowly, if you please.', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48494, 1, 0, 'There\'s a great deal that you do not understand, my dear Greely. That is why you are the assistant and I... I am Hobart Grapplehammer!', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48494, 0, 0, 'Prepare the Micro Mechachicken!', 14, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48496, 14, 0, 'We can both agree on that, doctor.', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48496, 13, 0, 'Now what\'s this about you \'always loved me\', Grapplehammer?', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48496, 11, 0, 'Is that egg on your face?', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48496, 12, 0, 'Hobart\'s jaw hits the ground.', 16, 0, 100, 0, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48496, 10, 0, 'We\'re still alive, Doctor Grapplehammer, despite your best efforts.', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48496, 9, 0, 'WHAT?!!!', 14, 0, 100, 5, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48496, 8, 0, 'It\'s the one on the far right! Hurry, Hobart! It\'s going to eggsplode!', 14, 0, 100, 5, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48496, 7, 0, 'Ya think? Hobart, the Micro Mechachicken is destabilizing. It\'s expanding!', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48496, 5, 0, 'Greely coughs.', 16, 0, 100, 0, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48496, 6, 0, 'Um, Hobart, you\'re at the controls.', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48496, 4, 0, 'The assistant shakes her head.', 16, 0, 100, 0, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48496, 3, 0, 'Yes, Doctor Grapplehammer.', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48496, 2, 0, 'Greely sighs and rolls her eyes.', 16, 0, 100, 0, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48496, 1, 0, 'There. One Micro Mechachicken. I\'ll never understand how you come up with these names.', 12, 0, 100, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48496, 0, 0, 'You don\'t have to yell, Hobart, I\'m standing right here. Sheesh!', 12, 0, 100, 6, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48514, 0, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Flee');
