INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48668, 0, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48727, 0, 2, 'Leave now... you do not deserve to suffer the crystal\'s wrath...', 12, 0, 50, 0, 0, 0, 0, 0, 'on Aggro Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48727, 0, 0, 'Thank you...', 12, 0, 50, 0, 0, 0, 0, 0, 'on Aggro Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48727, 0, 1, 'The crystal has returned.You must leave...', 12, 0, 50, 0, 0, 0, 0, 0, 'on Aggro Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48740, 1, 0, 'Forgive... me...', 12, 0, 100, 0, 0, 0, 0, 0, 'on Death Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48740, 0, 0, 'You don\'t understand! I had to study it! I HAD TO!', 12, 0, 100, 0, 0, 0, 0, 0, 'on Aggro Text');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48874, 0, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (48883, 0, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49141, 0, 0, 'Greetings, $n.', 12, 0, 100, 0, 0, 0, 49305, 0, 'Darnell');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49141, 1, 0, 'The Shadow Grave is this way.  Follow me, $n.', 12, 0, 100, 1, 0, 0, 49295, 0, 'Darnell');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49141, 2, 0, 'This way!', 12, 0, 100, 1, 0, 0, 49296, 0, 'Darnell');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49141, 3, 0, 'Now, where could those supplies be?', 12, 0, 100, 1, 0, 0, 49297, 0, 'Darnell');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49141, 4, 0, 'Maybe they\'re over here?', 12, 0, 100, 1, 0, 0, 49298, 0, 'Darnell');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49141, 5, 0, 'Hmm...', 12, 0, 100, 1, 0, 0, 49299, 0, 'Darnell');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49141, 6, 0, 'No, not over here.', 12, 0, 100, 1, 0, 0, 49300, 0, 'Darnell');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49141, 7, 0, 'Hey, give me a hand, $n!  I can\'t find the supplies that Mordo needed!', 12, 0, 100, 1, 0, 0, 49301, 0, 'Darnell');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49141, 8, 0, 'Let\'s see now... where could they be...', 12, 0, 100, 1, 0, 0, 49302, 0, 'Darnell');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49141, 9, 0, 'Nice work!  You\'ve found them.  Let\'s bring these back to Mordo.', 12, 0, 100, 1, 0, 0, 49303, 0, 'Darnell');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49141, 10, 0, 'I saw someone up there whose jaw fell off.  I wonder if Mordo can fix him up?', 12, 0, 100, 1, 0, 0, 49304, 0, 'Darnell');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49230, 0, 0, 'Who are you calling a monster? You\'re the monster! I\'m just a man who died.', 12, 0, 100, 1, 0, 0, 49345, 0, 'Marshal');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49231, 0, 0, 'I see. Well then, let\'s get to work, $n! The Dark Lady needs us, right?', 12, 0, 100, 1, 0, 0, 49351, 0, 'Valdred');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49231, 1, 0, 'Valdred Moray, reporting for duty, sir!', 14, 0, 100, 1, 0, 0, 49408, 0, 'Valdred');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49253, 0, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49255, 0, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49337, 0, 0, 'Hello again.', 12, 0, 100, 3, 0, 0, 49455, 0, 'Darnell');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49337, 1, 0, 'I know the way to Deathknell. Come with me!', 12, 0, 100, 273, 0, 0, 49456, 0, 'Darnell');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49337, 2, 0, 'Good, you\'re still here. Now, where\'s Deathguard Saltain?', 12, 0, 100, 1, 0, 0, 49457, 0, 'Darnell');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49337, 3, 0, 'Ah, here he is.', 12, 0, 100, 1, 0, 0, 49458, 0, 'Darnell');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49337, 4, 0, 'Let\'s get moving, $n. Saltain said that we\'d fine some corpses up here.', 12, 0, 100, 1, 0, 0, 49459, 0, 'Darnell');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49337, 5, 0, 'I think I see some corpses up ahead. Let\'s go, $n! You do the searching and fighting. I\'ll do the lifting.', 12, 0, 100, 0, 0, 0, 49460, 0, 'Darnell');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49422, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Enrage');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49476, 0, 0, 'RAZ, NOOOOOOOOOOO!!!!', 14, 0, 100, 0, 0, 0, 49597, 0, 'Finkle Einhorn');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49477, 0, 0, 'I fear time may not be on our side, Ilthalaine. The battle may carry to Teldrassil itself if we do not act soon.', 12, 0, 100, 0, 0, 0, 49682, 0, 'Huntress Sandrya Moonfall');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49478, 0, 0, 'We will leave you to your training, Ilthalaine. This is your area of expertise.', 12, 0, 100, 0, 0, 0, 49683, 0, 'Dentaria Silverglade');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49478, 1, 0, 'Study well, $n. We may need you fighting by our side before the next moon fills.', 12, 0, 100, 0, 0, 0, 49684, 0, 'Dentaria Silverglade');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49479, 3, 0, 'The ramp up to Aldrassil is just in sight over there. Circle around and find Tenaron up top.', 12, 0, 100, 0, 0, 0, 49667, 0, 'Dentaria Silverglade');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49479, 0, 0, 'Iverron\'s poison is cured, but it will take some time for him to recover.', 12, 0, 100, 0, 0, 0, 49668, 0, 'Dentaria Silverglade');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49479, 1, 0, 'Shadowthread Cave lies to the north. Be careful, it\'s dangerous there of late.', 12, 0, 100, 0, 0, 0, 49665, 0, 'Dentaria Silverglade');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49479, 2, 0, 'The moonwell is to the northeast, on the other side of the pool and up the hill.', 12, 0, 100, 0, 0, 0, 49666, 0, 'Dentaria Silverglade');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49480, 4, 0, 'I\'m going to stay and do what I can to free these spiders. Thank you for your help, $n.', 12, 0, 100, 0, 0, 0, 49549, 0, 'Tarindrella');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49480, 3, 0, 'My dear friends... I\'m sorry..', 12, 0, 100, 0, 0, 0, 49671, 0, 'Tarindrella');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49480, 1, 6, 'The corruption is buried deep within him.', 12, 0, 100, 0, 0, 0, 49669, 0, 'Tarindrella');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49480, 2, 0, 'This totem has been corrupting the eggs! It seems a greater threat looms. The Gnarlpine remain tainted by something most foul.', 12, 0, 100, 0, 0, 0, 49697, 0, 'Tarindrella');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49480, 1, 3, 'Corruption this deep could not have come from outside.', 12, 0, 100, 0, 0, 0, 49675, 0, 'Tarindrella');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49480, 1, 4, 'It almost seems as if he was born with this corruption inside of him.', 12, 0, 100, 0, 0, 0, 49676, 0, 'Tarindrella');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49480, 1, 5, 'They are surely unredeemable, but it still hurts to see them die.', 12, 0, 100, 0, 0, 0, 49670, 0, 'Tarindrella');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49480, 1, 1, 'His body screams for death, but his soul', 12, 0, 100, 0, 0, 0, 49672, 0, 'Tarindrella');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49480, 1, 2, 'I weep at these atrocities we commit. It is almost unbearable.', 12, 0, 100, 0, 0, 0, 49674, 0, 'Tarindrella');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49480, 1, 0, 'The corruption goes much further than these spiders. I fear the true source is not within this cave.', 12, 0, 100, 0, 0, 0, 49673, 0, 'Tarindrella');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (49480, 0, 0, 'You\'ve come to help, $c? Let us stay together for a while.', 12, 0, 100, 0, 0, 0, 49550, 0, 'Tarindrella');
