INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (55944, 1, 0, 'Hold the line, sailors! Your brothers depend on you!', 14, 0, 100, 22, 0, 27505, 59721, 0, 'Delora Lionheart');
