INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64879, 5, 0, 'That\'s a sad story!', 12, 0, 100, 1, 0, 0, 65658, 0, 'Yin to Lorewalker Amai');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64879, 3, 0, 'But we don\'t explore very much anymore, do we?', 12, 0, 100, 1, 0, 0, 65649, 0, 'Yin to Lorewalker Amai');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64879, 2, 0, 'But isn\'t Pandaria hidden somewhere? Nobody can find it!', 12, 0, 100, 1, 0, 0, 65646, 0, 'Yin to Lorewalker Amai');
