INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64879, 1, 0, '...Whenever he got hungry?', 12, 0, 100, 1, 0, 0, 65645, 0, 'Yin to Lorewalker Amai');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64880, 6, 0, 'No you don\'t. It\'s full of trolls.', 12, 0, 100, 1, 0, 0, 65652, 0, 'Nan to Lorewalker Amai');
