INSERT INTO `creature_text`(`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES 
(54944, 0, 0, 'You\'re about to get a mouthful of claws, $r. Ha HA!', 12, 0, 100, 0, 0, 0, 'Tian Pupil'),
(54944, 0, 1, 'Here I come!', 12, 0, 100, 0, 0, 0, 'Tian Pupil'),
(54944, 0, 2, 'Get your paws up where I can see \'em!', 12, 0, 100, 0, 0, 0, 'Tian Pupil'),
(54944, 0, 3, 'Go easy on the belly, alright?', 12, 0, 100, 0, 0, 0, 'Tian Pupil'),
(54944, 0, 4, 'Let\'s rumble!', 12, 0, 100, 0, 0, 0, 'Tian Pupil'),
(54944, 0, 5, 'Let\'s see how you hold up against my \"slow, weak punches\" technique!', 12, 0, 100, 0, 0, 0, 'Tian Pupil'),
(54944, 0, 6, 'Thank you for the opportunity of getting beaten up by you.', 12, 0, 100, 0, 0, 0, 'Tian Pupil'),
(54944, 0, 7, 'Yeah! Fightin\' time!', 12, 0, 100, 0, 0, 0, 'Tian Pupil'),
(54944, 1, 0, 'Good fight.', 12, 0, 100, 0, 0, 0, 'Tian Pupil'),
(54944, 1, 1, 'Good fight. I\'ll get you next time.', 12, 0, 100, 0, 0, 0, 'Tian Pupil'),
(54944, 1, 2, 'I don\'t think I ever stood a chance. See you around, then!', 12, 0, 100, 0, 0, 0, 'Tian Pupil'),
(54944, 1, 3, 'Man... I thought I had that one...', 12, 0, 100, 0, 0, 0, 'Tian Pupil'),
(54944, 1, 4, 'Master Stone Fist was right about you. You\'re good.', 12, 0, 100, 0, 0, 0, 'Tian Pupil'),
(54944, 1, 5, 'Nice moves!', 12, 0, 100, 0, 0, 0, 'Tian Pupil'),
(54944, 1, 6, 'You take this pretty seriously, don\'t you?', 12, 0, 100, 0, 0, 0, 'Tian Pupil');
