INSERT INTO `creature_text`(`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES 
(60901, 0, 0, 'NOOO!', 12, 0, 100, 0, 0, 0, 'Raufen - Ranger Rescue quest talk'),
(60901, 1, 0, 'Lin, heart of my heart! No, please, NO! It cannot be!', 12, 0, 100, 0, 0, 0, 'Raufen - Ranger Rescue quest talk'),
(60901, 2, 0, 'If only I had come sooner! If only Ban had listened to me!', 12, 0, 100, 0, 0, 0, 'Raufen - Ranger Rescue quest talk'),
(60901, 3, 0, 'Shhhhhh, my sweet darling, sleep now and be at peace.', 12, 0, 100, 0, 0, 0, 'Raufen - Ranger Rescue quest talk');
INSERT INTO `creature_text`(`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES
(42391, 1, 0, 'Who killed the Furlbrows? I\'ll tell you who killed the Furlbrows: KING VARIAN WRYNN. THAT\'S WHO! And he\'s killin\' the rest of us too. One bum at a time. The only thing I can tell you is that I saw some gnolls leavin\' the place a few hours before the law arrived.', 12, 0, 100, 0, 5000, 0, ''),
(42391, 2, 0, 'Between you, me, and the tree, murlocs killed the Furlbrows. Yep, saw \'em with my own two eyes. Think they\'d been casin\' the joint for days, maybe months. They left in a hurry once they got wind of \"Johnny Law\" and the idiot brigade over there...', 12, 0, 100, 0, 5000, 0, '');
